sys_variable_value.value:
(function execute(inputs, outputs) {
	var inputScope = inputs.flow_scope_sys_id;
    var newFlow = inputs.new_flow_sys_id;
    var catalogItem = inputs.catalog_item;

    //Update the catalog item 
   if(inputs.catalog_item != null){
    sn_flow.FlowTemplates.updateFlowActions(newFlow,'1', 'template_catalog_item', inputs.catalog_item);
   }
})(inputs, outputs);

